$((function(){$(document).on("click",".copy_to_clipboard_wrapper",(function(e){var o;var a=$(e.target);if(a.hasClass("input-group-text")&&!a.hasClass("copy_to_clipboard_wrapper")){return false}if(a.hasClass("input-group-text")||a.is("input")){a=a.parent(".copy_to_clipboard_wrapper").find("input");o=copyTextToClipboard(a.val())}else{if(a.attr("class")=="copy_to_clipboard_wrapper"){a=a.find("*")}a.select();try{o=document.execCommand("copy")}catch(e){o=false}a.blur()}var t;if(a.attr("class")=="copy_to_clipboard_wrapper"){t=a}else{t=a.parent(".copy_to_clipboard_wrapper").find("i.copy_to_clipboard_wrapper");if(!t.length){t=a.parent(".copy_to_clipboard_wrapper")}}if(o){$(".copy_to_clipboard_wrapper.copied").removeClass("copied");t.addClass("copied");setTimeout((function(){t.removeClass("copied")}),1e3)}else{t.addClass("copyfail")}}))}));function copyTextToClipboard(e){var o=document.createElement("textarea");o.value=e;o.setAttribute("readonly","");o.style={position:"absolute",visibility:"hidden"};document.body.appendChild(o);o.select();var a;try{a=document.execCommand("copy")}catch(e){a=false}document.body.removeChild(o);return a}